    if (event.originalTarget) {
        if (event.originalTarget.localName) {
            if(event.originalTarget.localName.toUpperCase() == 'A'){
                return true;
            }
        };
    };
    
    if(keyCode == 2){
        window.open(link, '_blank');
    }else{
        window.location.href = link;
    }
}
