            stores[i].getAttribute('onclick'),
            stores[i].getElementsByClassName('store-address')[0].innerHTML,
            stores[i].getElementsByClassName('store-product')[0].innerHTML
            ]) ;
        }
        var infowindow =  new google.maps.InfoWindow({
            content: ''
        });

        function setMarkers(map,storesdata){
            for (var i = 0; i < storesdata.length; i++) {
                var location = storesdata[i];
                var myLatLng = new google.maps.LatLng(location[0], location[1]);
                var marker = new google.maps.Marker({
                    position: myLatLng,
                    map: map
                });
                bindInfoWindow(marker, map, infowindow, '<div class = "MarkerPopUp" style="width: 300px;"><div class = "MarkerContext">'+location[2]+'<br>'+location[5]+'<br>'+location[6]+'<br>'+location[3]+'</div></div>',location[4]); 
                markers.push(marker);
              }
        }
        function bindInfoWindow(marker, map, infowindow, html,click) { 
           /* google.maps.event.addListener(marker, 'mouseover', function() {
                infowindow.setContent(html); 
                infowindow.open(map, marker);
            });

            google.maps.event.addListener(marker, 'mouseout', function() {
                infowindow.close();
            });*/

            google.maps.event.addListener(marker, 'click', function() {
                infowindow.setContent(html); 
                infowindow.open(map, marker);
            });
        } 
